Result of calling the Read tool: "     1→import { log } from "../utils/log";
     2→import * as JSON5 from 'json5';
     3→import { LLMProvider, UnifiedChatRequest, UnifiedMessage, UnifiedTool } from "../types/llm";
     4→import { Transformer } from "../types/transformer";
     5→import * as fs from 'fs';
     6→import * as path from 'path';
     7→
     8→// vvvvvvvvvvvv NEW HELPER FUNCTION vvvvvvvvvvvv
     9→/**
    10→ * Recursively cleans the properties of a JSON schema to keep only fields supported by Gemini.
    11→ * @param properties The properties object to clean.
    12→ * @returns A new, cleaned properties object.
    13→ */
    14→function cleanSchemaProperties(properties: Record<string, any>): Record<string, any> {
    15→  const cleanedProperties: Record<string, any> = {};
    16→
    17→  Object.keys(properties).forEach((key) => {
    18→    const prop = properties[key];
    19→    const cleanedProp: any = {};
    20→
    21→    // Strict whitelist of supported fields
    22→    cleanedProp.type = prop.type;
    23→    if (prop.description) cleanedProp.description = prop.description;
    24→    if (prop.enum) cleanedProp.enum = prop.enum;
    25→
    26→    // Recursive cleaning for array items
    27→    if (prop.type === 'array' && prop.items && typeof prop.items === 'object') {
    28→      cleanedProp.items = {
    29→        type: prop.items.type,
    30→      };
    31→      if (prop.items.description) {
    32→        cleanedProp.items.description = prop.items.description;
    33→      }
    34→      // *** THIS IS THE KEY RECURSIVE FIX ***
    35→      if (prop.items.properties) {
    36→        cleanedProp.items.properties = cleanSchemaProperties(prop.items.properties);
    37→      }
    38→       if (prop.items.required) {
    39→        cleanedProp.items.required = prop.items.required;
    40→      }
    41→    }
    42→
    43→    // Recursive cleaning for object properties
    44→    if (prop.type === 'object' && prop.properties) {
    45→        cleanedProp.properties = cleanSchemaProperties(prop.properties);
    46→    }
    47→
    48→    cleanedProperties[key] = cleanedProp;
    49→  });
    50→
    51→  return cleanedProperties;
    52→}
    53→// ^^^^^^^^^^^^ END OF HELPER FUNCTION ^^^^^^^^^^^^
    54→
    55→// Gemini API 类型定义
    56→interface GeminiPart {
    57→  text?: string;
    58→  thought?: boolean; // 添加思考块标志
    59→  functionCall?: {
    60→    id?: string;
    61→    name?: string;
    62→    args?: Record<string, any>;
    63→  };
    64→  functionResponse?: {
    65→    name: string;
    66→    response: {
    67→      name: string;
    68→      content: any;
    69→    };
    70→  };
    71→}
    72→
    73→interface GeminiContent {
    74→  role: "user" | "model";
    75→  parts: GeminiPart[];
    76→}
    77→
    78→
    79→
    80→interface GeminiFunctionDeclaration {
    81→  name: string;
    82→  description: string;
    83→  parameters: Record<string, any>;
    84→}
    85→
    86→interface GeminiTool {
    87→  functionDeclarations?: GeminiFunctionDeclaration[];
    88→}
    89→
    90→export class GeminiProTransformer implements Transformer {
    91→  name = "gemini-pro";
    92→
    93→//   endPoint = "/v1beta/models/:modelAndAction";
    94→
    95→  transformRequestIn(
    96→    request: UnifiedChatRequest,
    97→    provider: LLMProvider
    98→  ): Record<string, any> {
    99→    // --- Start of debug code ---
   100→    try {
   101→      const debugDir = path.join(process.cwd(), 'debug');
   102→      if (!fs.existsSync(debugDir)) {
   103→        fs.mkdirSync(debugDir, { recursive: true });
   104→      }
   105→      const filePath = path.join(debugDir, `gemini-pro-request-${Date.now()}.json`);
   106→      fs.writeFileSync(filePath, JSON.stringify(request, null, 2));
   107→      log.info(`[GEMINI_DEBUG] Saved incoming request to ${filePath}`);
   108→    } catch (e) {
   109→      log.error('[GEMINI_DEBUG] Failed to save request file', e);
   110→    }
   111→    // --- End of debug code ---
   112→
   113→    return {
   114→      body: {
   115→        contents: request.messages.map((message: UnifiedMessage) => {
   116→          let role: "user" | "model";
   117→          const parts: GeminiPart[] = [];
   118→
   119→          if (message.role === "assistant") {
   120→            role = "model";
   121→
   122→            // 处理 assistant 的文本内容
   123→            if (typeof message.content === "string") {
   124→              parts.push({ text: message.content });
   125→            } else if (Array.isArray(message.content)) {
   126→              parts.push(
   127→                ...message.content.map((content) => {
   128→                  if (content.type === "text") {
   129→                    return {
   130→                      text: content.text || "",
   131→                    };
   132→                  }
   133→                  return { text: "" };
   134→                }).filter(part => part.text !== "")
   135→              );
   136→            }
   137→
   138→            // 处理 assistant 发起的工具调用
   139→            if (Array.isArray(message.tool_calls)) {
   140→              parts.push(
   141→                ...message.tool_calls.map((toolCall) => {
   142→                  // 安全处理 arguments
   143→                  let args = {};
   144→                  try {
   145→                    if (typeof toolCall.function.arguments === "string") {
   146→                      args = JSON5.parse(toolCall.function.arguments || "{}");
   147→                    } else if (typeof toolCall.function.arguments === "object") {
   148→                      args = toolCall.function.arguments || {};
   149→                    }
   150→                  } catch (error) {
   151→                    log.warn('⚠️ [GEMINI_TOOL_ARGS_PARSE_ERROR] 工具参数解析失败:', error);
   152→                    args = {};
   153→                  }
   154→                  
   155→                  return {
   156→                    functionCall: {
   157→                      id: toolCall.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   158→                      name: toolCall.function.name,
   159→                      args: args,
   160→                    },
   161→                  };
   162→                })
   163→              );
   164→            }
   165→
   166→          } else if (message.role === "tool") {
   167→            role = "user"; // 角色映射保持不变
   168→
   169→            const toolCallId = message.tool_call_id;
   170→            let functionName: string | undefined;
   171→
   172→            // 关键修正：在整个消息历史中向后查找，根据 tool_call_id 找回函数名
   173→            if (toolCallId) {
   174→              for (const prevMessage of request.messages) {
   175→                if (prevMessage.role === "assistant" && Array.isArray(prevMessage.tool_calls)) {
   176→                  const originalToolCall = prevMessage.tool_calls.find(tc => tc.id === toolCallId);
   177→                  if (originalToolCall) {
   178→                    functionName = originalToolCall.function.name;
   179→                    break; // 找到后立即退出循环
   180→                  }
   181→                }
   182→              }
   183→            }
   184→
   185→            if (functionName) {
   186→              // ---- 理想情况：成功找回函数名，构建标准的 functionResponse ----
   187→              let toolResponseContent;
   188→              try {
   189→                toolResponseContent = typeof message.content === 'string'
   190→                  ? JSON5.parse(message.content)
   191→                  : message.content;
   192→              } catch (e) {
   193→                toolResponseContent = { result: message.content };
   194→              }
   195→
   196→              parts.push({
   197→                functionResponse: {
   198→                  name: functionName,
   199→                  response: {
   200→                    name: functionName,
   201→                    content: toolResponseContent,
   202→                  },
   203→                },
   204→              });
   205→            } else {
   206→              // ---- 降级策略：没能找回函数名，作为纯文本发送以保证流程不中断 ----
   207→              log.warn(`⚠️ [GEMINI_TOOL_RESCUE] Could not find function name for tool_call_id '${toolCallId}'. Sending as plain text.`);
   208→              parts.push({
   209→                text: typeof message.content === 'string' ? message.content :
   210→                  JSON.stringify(message.content)
   211→              });
   212→            }
   213→          } else { // 包括 "user" 和 "system"
   214→            role = "user";
   215→
   216→            // 处理 user 或 system 的文本内容
   217→            if (typeof message.content === "string") {
   218→              parts.push({ text: message.content });
   219→            } else if (Array.isArray(message.content)) {
   220→              parts.push(
   221→                ...message.content.map((content) => {
   222→                  if (content.type === "text") {
   223→                    return {
   224→                      text: content.text || "",
   225→                    };
   226→                  }
   227→                  return { text: "" };
   228→                }).filter(part => part.text !== "")
   229→              );
   230→            }
   231→          }
   232→          
   233→          return {
   234→            role,
   235→            parts,
   236→          };
   237→        }),
   238→        tools: request.tools && request.tools.length > 0 ? [
   239→          {
   240→            functionDeclarations:
   241→              request.tools.map((tool: UnifiedTool) => {
   242→                const cleanedParameters: any = {
   243→                  type: "object",
   244→                };
   245→
   246→                if (tool.function.parameters) {
   247→                  if (tool.function.parameters.properties) {
   248→                    // Use the new recursive helper function
   249→                    cleanedParameters.properties = cleanSchemaProperties(tool.function.parameters.properties);
   250→                  }
   251→                  if (Array.isArray(tool.function.parameters.required) &&
   252→                      tool.function.parameters.required.length > 0) {
   253→                    cleanedParameters.required = tool.function.parameters.required;
   254→                  }
   255→                }
   256→
   257→                return {
   258→                  name: tool.function.name,
   259→                  description: tool.function.description,
   260→                  parameters: cleanedParameters,
   261→                };
   262→              }) || [],
   263→          },
   264→        ] : undefined, // 如果没有工具，则不发送该字段
   265→        generationConfig: {
   266→          thinkingConfig: {
   267→            includeThoughts: true,
   268→            thinkingBudget: 10000
   269→          },
   270→          ...(request.max_tokens && { maxOutputTokens: request.max_tokens }),
   271→          ...(request.temperature && { temperature: request.temperature })
   272→        },
   273→      },
   274→      config: {
   275→        url: new URL(
   276→          `./${request.model}:${
   277→            request.stream ? "streamGenerateContent?alt=sse" : "generateContent"
   278→          }`,
   279→          provider.baseUrl
   280→        ),
   281→        headers: {
   282→          "x-goog-api-key": provider.apiKey,
   283→          Authorization: undefined,
   284→        },
   285→      },
   286→    };
   287→  }
   288→
   289→  transformRequestOut(request: Record<string, any>): UnifiedChatRequest {
   290→    const contents: GeminiContent[] = request.contents || [];
   291→    const tools: GeminiTool[] = request.tools || [];
   292→    const model: string = request.model;
   293→    const max_tokens: number | undefined = request.max_tokens;
   294→    const temperature: number | undefined = request.temperature;
   295→    const stream: boolean | undefined = request.stream;
   296→    const tool_choice = request.tool_choice;
   297→
   298→    const unifiedChatRequest: UnifiedChatRequest = {
   299→      messages: [],
   300→      model,
   301→      max_tokens,
   302→      temperature,
   303→      stream,
   304→      tool_choice,
   305→    };
   306→
   307→    contents.forEach((content) => {
   308→      const message: UnifiedMessage = {
   309→        role: content.role === "model" ? "assistant" : "user",
   310→        content: content.parts
   311→          .filter(part => part.text)
   312→          .map(part => ({
   313→            type: "text" as const,
   314→            text: part.text || "",
   315→          })),
   316→      };
   317→      
   318→      // 处理工具调用
   319→      const toolCalls = content.parts
   320→        .filter(part => part.functionCall)
   321→        .map(part => ({
   322→          id: part.functionCall?.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   323→          type: "function" as const,
   324→          function: {
   325→            name: part.functionCall?.name || "",
   326→            arguments: JSON.stringify(part.functionCall?.args || {}),
   327→          },
   328→        }));
   329→      
   330→      if (toolCalls.length > 0) {
   331→        message.tool_calls = toolCalls;
   332→      }
   333→      
   334→      unifiedChatRequest.messages.push(message);
   335→    });
   336→
   337→    if (tools.length > 0) {
   338→      unifiedChatRequest.tools = [];
   339→      tools.forEach((tool) => {
   340→        if (tool.functionDeclarations) {
   341→          tool.functionDeclarations.forEach((funcDecl) => {
   342→            unifiedChatRequest.tools!.push({
   343→              type: "function",
   344→              function: {
   345→                name: funcDecl.name,
   346→                description: funcDecl.description,
   347→                parameters: {
   348→                  type: "object",
   349→                  properties: funcDecl.parameters.properties || {},
   350→                  required: funcDecl.parameters.required || [],
   351→                  additionalProperties: funcDecl.parameters.additionalProperties
   352→                },
   353→              },
   354→            });
   355→          });
   356→        }
   357→      });
   358→    }
   359→
   360→    return unifiedChatRequest;
   361→  }
   362→
   363→  async transformResponseOut(response: Response): Promise<Response> {
   364→    if (response.headers.get("Content-Type")?.includes("application/json")) {
   365→      return this.handleNonStreamResponse(response);
   366→    } else if (response.headers.get("Content-Type")?.includes("stream")) {
   367→      return this.handleStreamResponse(response);
   368→    }
   369→    return response;
   370→  }
   371→
   372→  private async handleNonStreamResponse(response: Response): Promise<Response> {
   373→    // 检查响应状态
   374→    if (!response.ok) {
   375→      const errorText = await response.text();
   376→      log.error('❌ [GEMINI_ERROR] Gemini API 错误响应:', response.status, response.statusText);
   377→      log.error('❌ [GEMINI_ERROR] 错误详情:', errorText);
   378→      throw new Error(`Gemini API error: ${response.status} ${response.statusText} - ${errorText}`);
   379→    }
   380→    
   381→    // 记录原始响应内容
   382→    const responseClone = response.clone();
   383→    const rawResponseText = await responseClone.text();
   384→    log.info('🔍 [GEMINI_RAW_RESPONSE] 服务器原始响应:', rawResponseText);
   385→    
   386→    const jsonResponse: any = JSON5.parse(rawResponseText);
   387→    
   388→    // 检查是否有错误信息
   389→    if (jsonResponse.error) {
   390→      log.error('❌ [GEMINI_ERROR] Gemini API 返回错误:', jsonResponse.error);
   391→      throw new Error(`Gemini API error: ${jsonResponse.error.message || 'Unknown error'}`);
   392→    }
   393→    
   394→    if (!jsonResponse.candidates || !jsonResponse.candidates[0]) {
   395→      throw new Error("Invalid Gemini response format");
   396→    }
   397→    
   398→    // 检查 finishReason 是否为错误状态
   399→    const candidate = jsonResponse.candidates[0];
   400→    if (candidate.finishReason === "MALFORMED_FUNCTION_CALL") {
   401→      log.warn('⚠️ [GEMINI_MALFORMED_FUNCTION] 检测到工具调用格式错误');
   402→      // 返回一个空的成功响应，而不是抛出错误
   403→      const errorRes = {
   404→        id: jsonResponse.responseId || `chatcmpl-${Date.now()}`,
   405→        choices: [
   406→          {
   407→            finish_reason: "stop",
   408→            index: 0,
   409→            message: {
   410→              content: "抱歉，工具调用格式有误，无法执行。",
   411→              role: "assistant" as const,
   412→            },
   413→          },
   414→        ],
   415→        created: Math.floor(Date.now() / 1000),
   416→        model: jsonResponse.modelVersion || "gemini-pro",
   417→        object: "chat.completion" as const,
   418→        usage: jsonResponse.usageMetadata ? {
   419→          completion_tokens: jsonResponse.usageMetadata.candidatesTokenCount,
   420→          prompt_tokens: jsonResponse.usageMetadata.promptTokenCount,
   421→          total_tokens: jsonResponse.usageMetadata.totalTokenCount,
   422→        } : undefined,
   423→      };
   424→      
   425→      return new Response(JSON.stringify(errorRes), {
   426→        status: 200, // 返回200而不是错误状态
   427→        statusText: "OK",
   428→        headers: response.headers,
   429→      });
   430→    }
   431→    const tool_calls = candidate.content.parts
   432→      .filter((part: GeminiPart) => part.functionCall)
   433→      .map((part: GeminiPart) => {
   434→        // 安全处理 arguments
   435→        let args = part.functionCall?.args || {};
   436→        let argsString = "";
   437→        
   438→        try {
   439→          if (typeof args === "string") {
   440→            // 如果已经是字符串，直接使用
   441→            argsString = args;
   442→          } else {
   443→            // 如果是对象，序列化为字符串
   444→            argsString = JSON.stringify(args);
   445→          }
   446→        } catch (error) {
   447→          log.warn('⚠️ [GEMINI_TOOL_ARGS_ERROR] 工具参数序列化失败:', error);
   448→          argsString = "{}";
   449→        }
   450→        
   451→        return {
   452→          id: part.functionCall?.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   453→          type: "function" as const,
   454→          function: {
   455→            name: part.functionCall?.name || "",
   456→            arguments: argsString,
   457→          },
   458→        };
   459→      });
   460→
   461→    const res = {
   462→      id: jsonResponse.responseId || `chatcmpl-${Date.now()}`,
   463→      choices: [
   464→        {
   465→          finish_reason: candidate.finishReason?.toLowerCase() || null,
   466→          index: 0,
   467→          message: {
   468→            content: candidate.content.parts
   469→              .filter((part: GeminiPart) => {
   470→                // 过滤思考块，只做日志
   471→                if (part.thought === true) {
   472→                  log.info(`🧠 [GEMINI_THOUGHT] 捕获到思考块 (非流式): "${part.text}"`);
   473→                  return false;
   474→                }
   475→                return !!part.text;
   476→              })
   477→              .map((part: GeminiPart) => part.text)
   478→              .join(""),
   479→            role: "assistant" as const,
   480→            tool_calls: tool_calls.length > 0 ? tool_calls : undefined,
   481→          },
   482→        },
   483→      ],
   484→      created: Math.floor(Date.now() / 1000),
   485→      model: jsonResponse.modelVersion || "gemini-pro",
   486→      object: "chat.completion" as const,
   487→      usage: jsonResponse.usageMetadata ? {
   488→        completion_tokens: jsonResponse.usageMetadata.candidatesTokenCount,
   489→        prompt_tokens: jsonResponse.usageMetadata.promptTokenCount,
   490→        total_tokens: jsonResponse.usageMetadata.totalTokenCount,
   491→      } : undefined,
   492→    };
   493→
   494→    return new Response(JSON.stringify(res), {
   495→      status: response.status,
   496→      statusText: response.statusText,
   497→      headers: response.headers,
   498→    });
   499→  }
   500→
   501→  private handleStreamResponse(response: Response): Response {
   502→    if (!response.body) {
   503→      return response;
   504→    }
   505→
   506→    const decoder = new TextDecoder();
   507→    const encoder = new TextEncoder();
   508→    const stream = new ReadableStream({
   509→      async start(controller) {
   510→        const reader = response.body!.getReader();
   511→        let buffer = "";
   512→        let usageMetadata: any = null;
   513→        let hasInjectedThinking = false;
   514→        let hasProducedContent = false; // <-- 添加内容追踪标志
   515→        let blockCounter = 0; // 🔍 添加数据块计数器
   516→
   517→        // SSE 解析状态机
   518→        let state = 'WAITING_DATA'; // WAITING_DATA, IN_DATA, WAITING_END
   519→        let currentData = "";
   520→        let dataLines: string[] = [];
   521→
   522→        try {
   523→          while (true) {
   524→            const { done, value } = await reader.read();
   525→            if (done) {
   526→              // 流结束，处理最后的缓冲区
   527→              if (buffer.trim()) {
   528→                log.warn('⚠️ [GEMINI_STREAM_WARN] 流结束时缓冲区中仍有未处理数据:', buffer.substring(0, 200) + '...');
   529→                await processBuffer(buffer, true); // 强制处理
   530→              }
   531→              
   532→              // <-- 在这里注入空内容块
   533→              if (!hasProducedContent) {
   534→                log.info('🟡 [GEMINI_EMPTY_STREAM] 为保证消息合法，注入一个空内容块');
   535→                const emptyContentChunk = {
   536→                  choices: [{
   537→                    delta: { role: "assistant", content: "" },
   538→                    index: 0,
   539→                    finish_reason: null,
   540→                  }],
   541→                  created: parseInt(String(Date.now() / 1000)),
   542→                  id: "empty_content_fix",
   543→                  model: "gemini-pro",
   544→                  object: "chat.completion.chunk",
   545→                };
   546→                controller.enqueue(encoder.encode(`data: ${JSON.stringify(emptyContentChunk)}\n\n`));
   547→              }
   548→              
   549→              // 发送最终的结束块
   550→              log.info('🏁 [GEMINI_STREAM_END] 流真正结束，发送最终块');
   551→              const finalRes: any = {
   552→                choices: [
   553→                  {
   554→                    delta: {
   555→                      role: "assistant",
   556→                      content: null,
   557→                    },
   558→                    finish_reason: "stop",
   559→                    index: 0,
   560→                    logprobs: null,
   561→                  },
   562→                ],
   563→                created: parseInt(new Date().getTime() / 1000 + "", 10),
   564→                id: "final",
   565→                model: "gemini-pro",
   566→                object: "chat.completion.chunk",
   567→                system_fingerprint: "fp_a49d71b8a1",
   568→              };
   569→              
   570→              if (usageMetadata) {
   571→                finalRes.usage = {
   572→                  completion_tokens: usageMetadata.candidatesTokenCount || 0,
   573→                  prompt_tokens: usageMetadata.promptTokenCount || 0,
   574→                  total_tokens: usageMetadata.totalTokenCount || 0,
   575→                };
   576→              }
   577→              
   578→              const finalChunk = `data: ${JSON.stringify(finalRes)}\n\n`;
   579→              log.info('🏁 [GEMINI_FINAL_CHUNK] 最终块内容:', finalChunk);
   580→              
   581→              controller.enqueue(encoder.encode(finalChunk));
   582→              break;
   583→            }
   584→
   585→            // 将新数据追加到缓冲区
   586→            const newData = decoder.decode(value, { stream: true });
   587→            // console.log("Gemini Raw Stream Data:", newData);
   588→            buffer += newData;
   589→            
   590→            // 🔍 只记录包含思考token的数据块
   591→            // if (newData.includes('thoughtsTokenCount') && newData.includes('"thoughtsTokenCount":')) {
   592→            //   log.info('🔍 [GEMINI_RAW_STREAM] 收到包含思考token的数据');
   593→            // }
   594→            
   595→            // 处理缓冲区
   596→            await processBuffer(buffer, false);
   597→          }
   598→        } catch (error) {
   599→          log.error('❌ [GEMINI_STREAM_ERROR] 流处理错误:', error);
   600→          // 发送错误消息给客户端
   601→          try {
   602→            const errorRes = {
   603→              choices: [
   604→                {
   605→                  delta: {
   606→                    role: "assistant",
   607→                    content: "抱歉，处理响应时出现错误。",
   608→                  },
   609→                  finish_reason: "stop",
   610→                  index: 0,
   611→                  logprobs: null,
   612→                },
   613→              ],
   614→              created: parseInt(new Date().getTime() / 1000 + "", 10),
   615→              id: "",
   616→              model: "",
   617→              object: "chat.completion.chunk",
   618→              system_fingerprint: "fp_a49d71b8a1",
   619→            };
   620→            controller.enqueue(encoder.encode(`data: ${JSON.stringify(errorRes)}\n\n`));
   621→          } catch (finalError) {
