   623→          }
   624→        } finally {
   625→          controller.close();
   626→        }
   627→
   628→        // 处理缓冲区的函数
   629→        async function processBuffer(bufferData: string, isEnd: boolean) {
   630→          // 🔍 只记录包含思考token的缓冲区
   631→          // if (bufferData.includes('thoughtsTokenCount') && bufferData.includes('"thoughtsTokenCount":')) {
   632→          //   log.info('🔍 [GEMINI_BUFFER_PROCESS] 处理包含思考token的缓冲区');
   633→          // }
   634→          const lines = bufferData.split('\n');
   635→          let remainingBuffer = "";
   636→          
   637→          for (let i = 0; i < lines.length; i++) {
   638→            const line = lines[i];
   639→            
   640→            switch (state) {
   641→              case 'WAITING_DATA':
   642→                if (line.startsWith('data: ')) {
   643→                  // 开始新的data块
   644→                  currentData = line.slice(6); // 移除 'data: ' 前缀
   645→                  dataLines = [currentData];
   646→                  state = 'IN_DATA';
   647→                  // 🔍 只记录包含思考token的数据块开始
   648→                  // if (currentData.includes('thoughtsTokenCount')) {
   649→                  //   log.info('🔍 [GEMINI_DATA_START] 开始包含思考token的数据块');
   650→                  // }
   651→                } else if (line.trim() !== '') {
   652→                  // 非空行但不是data开头，可能是其他SSE字段，忽略
   653→                  log.warn('⚠️ [GEMINI_UNKNOWN_LINE] 未知行:', line.substring(0, 100) + '...');
   654→                }
   655→                break;
   656→                
   657→              case 'IN_DATA':
   658→                if (line.startsWith('data: ')) {
   659→                  // 遇到新的data块，先处理当前块
   660→                  await processCompleteDataBlock();
   661→                  
   662→                  // 开始新的data块
   663→                  currentData = line.slice(6);
   664→                  dataLines = [currentData];
   665→                } else if (line.trim() === '') {
   666→                  // 空行表示data块结束
   667→                  // 🔍 只记录包含思考token的数据块结束
   668→                  // if (dataLines.some(line => line.includes('thoughtsTokenCount'))) {
   669→                  //   log.info('🔍 [GEMINI_DATA_END] 包含思考token的数据块结束，准备处理');
   670→                  // }
   671→                  await processCompleteDataBlock();
   672→                  state = 'WAITING_DATA';
   673→                } else {
   674→                  // 继续当前data块
   675→                  dataLines.push(line);
   676→                }
   677→                break;
   678→            }
   679→          }
   680→          
   681→          // 如果不是流结束，保留最后一行作为缓冲区（可能不完整）
   682→          if (!isEnd && lines.length > 0) {
   683→            const lastLine = lines[lines.length - 1];
   684→            if (state === 'IN_DATA' && !lastLine.startsWith('data: ') && lastLine.trim() !== '') {
   685→              // 当前在data块中，最后一行可能不完整，保留在缓冲区
   686→              remainingBuffer = lastLine;
   687→            } else if (state === 'WAITING_DATA' && lastLine.startsWith('data: ')) {
   688→              // 最后一行是data开头，但可能不完整
   689→              remainingBuffer = lastLine;
   690→            }
   691→          }
   692→          
   693→          // 更新缓冲区
   694→          buffer = remainingBuffer;
   695→        }
   696→        
   697→        // 处理完整的data块
   698→        async function processCompleteDataBlock() {
   699→          
   700→          if (dataLines.length === 0) return;
   701→          
   702→          // 合并所有行
   703→          const jsonStr = dataLines.join('\n').trim();
   704→          // log.info(`RAW_GEMINI_CHUNK: ${jsonStr}`);
   705→          if (!jsonStr) return;
   706→          
   707→          blockCounter++; // 🔍 增加数据块计数器
   708→          // log.info('🔍 [GEMINI_PROCESSING_BLOCK] 处理第' + blockCounter + '个data块，长度:', jsonStr.length);
   709→          
   710→          let chunk: any;
   711→          try {
   712→            chunk = JSON5.parse(jsonStr);
   713→            
   714→                      // 🔍 只记录包含思考token的数据块
   715→          // if (chunk.usageMetadata && chunk.usageMetadata.thoughtsTokenCount > 0) {
   716→          //   log.info('🧠 [GEMINI_THINKING_DETECTED] 第' + blockCounter + '块: 思考token=' + chunk.usageMetadata.thoughtsTokenCount + ', parts=' + (chunk.candidates?.[0]?.content?.parts?.length || 0));
   717→          // }
   718→          
   719→          if (chunk.usageMetadata) {
   720→            usageMetadata = chunk.usageMetadata;
   721→          }
   722→          } catch (parseError: any) {
   723→            log.error('❌ [GEMINI_JSON_PARSE_ERROR] JSON解析失败:', parseError);
   724→            log.error('❌ [GEMINI_JSON_CONTENT] 内容:', jsonStr.substring(0, 300) + '...');
   725→            return; // 跳过这个块
   726→          }
   727→
   728→          if (!hasInjectedThinking && (chunk.candidates?.[0]?.content?.parts || chunk.usageMetadata)) {
   729→            hasInjectedThinking = true;
   730→            const thinkingRes = {
   731→              choices: [
   732→                {
   733→                  delta: {
   734→                    role: "assistant",
   735→                    thinking: true,
   736→                  },
   737→                  finish_reason: null,
   738→                  index: 0,
   739→                },
   740→              ],
   741→              created: parseInt(new Date().getTime() / 1000 + "", 10),
   742→              id: (chunk as any).responseId || "thinking_id",
   743→              model: (chunk as any).modelVersion || "gemini-pro",
   744→              object: "chat.completion.chunk",
   745→              system_fingerprint: "fp_a49d71b8a1",
