   749→          }
   750→
   751→          // 检查是否有 MALFORMED_FUNCTION_CALL 错误
   752→          if ((chunk as any).candidates?.[0]?.finishReason === "MALFORMED_FUNCTION_CALL") {
   753→            log.warn('⚠️ [GEMINI_MALFORMED_FUNCTION_STREAM] 检测到流式响应中的工具调用格式错误');
   754→            const errorRes = {
   755→              choices: [
   756→                {
   757→                  delta: {
   758→                    role: "assistant",
   759→                    content: "抱歉，工具调用格式有误，无法执行。",
   760→                  },
   761→                  finish_reason: "stop",
   762→                  index: 0,
   763→                  logprobs: null,
   764→                },
   765→              ],
   766→              created: parseInt(new Date().getTime() / 1000 + "", 10),
   767→              id: "",
   768→              model: "",
   769→              object: "chat.completion.chunk",
   770→              system_fingerprint: "fp_a49d71b8a1",
   771→            };
   772→            controller.enqueue(encoder.encode(`data: ${JSON.stringify(errorRes)}\n\n`));
