   776→          let content = "";
   777→          const tool_calls: any[] = [];
   778→          const parts = (chunk as any).candidates?.[0]?.content?.parts || [];
   779→
   780→          // 🔍 只记录包含思考内容的关键信息
   781→          const hasThinkingParts = parts.some((part: any) => part.thought === true);
   782→          if (hasThinkingParts) {
   783→            // log.info('🧠 [GEMINI_THINKING_PARTS] 第' + blockCounter + '块包含思考内容!');
   784→            parts.forEach((part: any, index: number) => {
   785→              // if (part.thought === true) {
   786→              //   log.info('🧠 [GEMINI_THINKING_TEXT] Part ' + index + ':', '"' + part.text + '"');
