   795→
   796→              // 1. 构建一个下游 anthropic.transformer 能理解的 "thinking" chunk
   797→              const thinkingChunk = {
   798→                choices: [
   799→                  {
   800→                    delta: {
   801→                      // 2. 核心：创建一个 thinking 对象，并将思考内容放入
   802→                      thinking: {
   803→                        content: part.text || ""
   804→                      }
   805→                    },
   806→                    index: 0,
   807→                    finish_reason: null,
   808→                  },
   809→                ],
   810→                // 补全其他字段，使其成为一个合法的流式块
   811→                id: chunk.responseId ? `${chunk.responseId}-th` : `chatcmpl-th-${Date.now()}`,
   812→                model: chunk.modelVersion || "gemini-pro",
   813→                object: "chat.completion.chunk",
   814→              };
   815→
   816→              // 3. 将这个新构建的 chunk 发送到流中，给下游的转换器处理
   817→              controller.enqueue(encoder.encode(`data: ${JSON.stringify(thinkingChunk)}\n\n`));
