   864→                  tool_calls: tool_calls.length > 0 ? tool_calls : undefined,
   865→                },
   866→                finish_reason: null,
   867→                index: (chunk as any).candidates[0].index || tool_calls.length > 0 ? 1 : 0,
   868→                logprobs: null,
   869→              },
   870→            ],
   871→            created: parseInt(new Date().getTime() / 1000 + "", 10),
   872→            id: (chunk as any).responseId || "",
   873→            model: (chunk as any).modelVersion || "",
   874→            object: "chat.completion.chunk",
   875→            system_fingerprint: "fp_a49d71b8a1",
   876→          };
   877→
   878→          if ((chunk as any).usageMetadata) {
   879→            res.usage = {
   880→              completion_tokens: (chunk as any).usageMetadata.candidatesTokenCount,
   881→              prompt_tokens: (chunk as any).usageMetadata.promptTokenCount,
   882→              total_tokens: (chunk as any).usageMetadata.totalTokenCount,
   883→            };
   884→          }
   885→
   886→          controller.enqueue(encoder.encode(`data: ${JSON.stringify(res)}\n\n`));
   887→        }
   888→      },
   889→    });
   890→
   891→    return new Response(stream, {
   892→      status: response.status,
