     1→import { log } from "../utils/log";
     2→import * as JSON5 from 'json5';
     3→import { LLMProvider, UnifiedChatRequest, UnifiedMessage, UnifiedTool } from "../types/llm";
     4→import { Transformer } from "../types/transformer";
     5→
     6→// vvvvvvvvvvvv NEW HELPER FUNCTION vvvvvvvvvvvv
     7→/**
     8→ * Recursively cleans the properties of a JSON schema to keep only fields supported by Gemini.
     9→ * @param properties The properties object to clean.
    10→ * @returns A new, cleaned properties object.
    11→ */
    12→function cleanSchemaProperties(properties: Record<string, any>): Record<string, any> {
    13→  const cleanedProperties: Record<string, any> = {};
    14→
    15→  Object.keys(properties).forEach((key) => {
    16→    const prop = properties[key];
    17→    const cleanedProp: any = {};
    18→
    19→    // Strict whitelist of supported fields
    20→    cleanedProp.type = prop.type;
    21→    if (prop.description) cleanedProp.description = prop.description;
    22→    if (prop.enum) cleanedProp.enum = prop.enum;
    23→
    24→    // Recursive cleaning for array items
    25→    if (prop.type === 'array' && prop.items && typeof prop.items === 'object') {
    26→      cleanedProp.items = {
    27→        type: prop.items.type,
    28→      };
    29→      if (prop.items.description) {
    30→        cleanedProp.items.description = prop.items.description;
    31→      }
    32→      // *** THIS IS THE KEY RECURSIVE FIX ***
    33→      if (prop.items.properties) {
    34→        cleanedProp.items.properties = cleanSchemaProperties(prop.items.properties);
    35→      }
    36→       if (prop.items.required) {
    37→        cleanedProp.items.required = prop.items.required;
    38→      }
    39→    }
    40→
    41→    // Recursive cleaning for object properties
    42→    if (prop.type === 'object' && prop.properties) {
    43→        cleanedProp.properties = cleanSchemaProperties(prop.properties);
    44→    }
    45→
    46→    cleanedProperties[key] = cleanedProp;
    47→  });
    48→
    49→  return cleanedProperties;
    50→}
    51→// ^^^^^^^^^^^^ END OF HELPER FUNCTION ^^^^^^^^^^^^
    52→
    53→// Gemini API 类型定义
    54→interface GeminiPart {
    55→  text?: string;
    56→  thought?: boolean; // 添加思考块标志
    57→  functionCall?: {
    58→    id?: string;
    59→    name?: string;
    60→    args?: Record<string, any>;
    61→  };
    62→  functionResponse?: {
    63→    name: string;
    64→    response: {
    65→      name: string;
    66→      content: any;
    67→    };
    68→  };
    69→}
    70→
    71→interface GeminiContent {
    72→  role: "user" | "model";
    73→  parts: GeminiPart[];
    74→}
    75→
    76→
    77→
    78→interface GeminiFunctionDeclaration {
    79→  name: string;
    80→  description: string;
    81→  parameters: Record<string, any>;
    82→}
    83→
    84→interface GeminiTool {
    85→  functionDeclarations?: GeminiFunctionDeclaration[];
    86→}
    87→
    88→export class GeminiProTransformer implements Transformer {
    89→  name = "gemini-pro";
    90→
    91→//   endPoint = "/v1beta/models/:modelAndAction";
    92→
    93→  transformRequestIn(
    94→    request: UnifiedChatRequest,
    95→    provider: LLMProvider
    96→  ): Record<string, any> {
    97→    return {
    98→      body: {
    99→        contents: request.messages.map((message: UnifiedMessage) => {
   100→          let role: "user" | "model";
   101→          const parts: GeminiPart[] = [];
   102→
   103→          if (message.role === "assistant") {
   104→            role = "model";
   105→
   106→            // 处理 assistant 的文本内容
   107→            if (typeof message.content === "string") {
   108→              parts.push({ text: message.content });
   109→            } else if (Array.isArray(message.content)) {
   110→              parts.push(
   111→                ...message.content.map((content) => {
   112→                  if (content.type === "text") {
   113→                    return {
   114→                      text: content.text || "",
   115→                    };
   116→                  }
   117→                  return { text: "" };
   118→                }).filter(part => part.text !== "")
   119→              );
   120→            }
   121→
   122→            // 处理 assistant 发起的工具调用
   123→            if (Array.isArray(message.tool_calls)) {
   124→              parts.push(
   125→                ...message.tool_calls.map((toolCall) => {
   126→                  // 安全处理 arguments
   127→                  let args = {};
   128→                  try {
   129→                    if (typeof toolCall.function.arguments === "string") {
   130→                      args = JSON5.parse(toolCall.function.arguments || "{}");
   131→                    } else if (typeof toolCall.function.arguments === "object") {
   132→                      args = toolCall.function.arguments || {};
   133→                    }
   134→                  } catch (error) {
   135→                    log.warn('⚠️ [GEMINI_TOOL_ARGS_PARSE_ERROR] 工具参数解析失败:', error);
   136→                    args = {};
   137→                  }
   138→                  
   139→                  return {
   140→                    functionCall: {
   141→                      id: toolCall.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   142→                      name: toolCall.function.name,
   143→                      args: args,
   144→                    },
   145→                  };
   146→                })
   147→              );
   148→            }
   149→
   150→          } else if (message.role === "tool") {
   151→            role = "user"; // 角色映射保持不变
   152→
   153→            const toolCallId = message.tool_call_id;
   154→            let functionName: string | undefined;
   155→
   156→            // 关键修正：在整个消息历史中向后查找，根据 tool_call_id 找回函数名
   157→            if (toolCallId) {
   158→              for (const prevMessage of request.messages) {
   159→                if (prevMessage.role === "assistant" && Array.isArray(prevMessage.tool_calls)) {
   160→                  const originalToolCall = prevMessage.tool_calls.find(tc => tc.id === toolCallId);
   161→                  if (originalToolCall) {
   162→                    functionName = originalToolCall.function.name;
   163→                    break; // 找到后立即退出循环
   164→                  }
   165→                }
   166→              }
   167→            }
   168→
   169→            if (functionName) {
   170→              // ---- 理想情况：成功找回函数名，构建标准的 functionResponse ----
   171→              let toolResponseContent;
   172→              try {
   173→                toolResponseContent = typeof message.content === 'string'
   174→                  ? JSON5.parse(message.content)
   175→                  : message.content;
   176→              } catch (e) {
   177→                toolResponseContent = { result: message.content };
   178→              }
   179→
   180→              parts.push({
   181→                functionResponse: {
   182→                  name: functionName,
   183→                  response: {
   184→                    name: functionName,
   185→                    content: toolResponseContent,
   186→                  },
   187→                },
   188→              });
   189→            } else {
   190→              // ---- 降级策略：没能找回函数名，作为纯文本发送以保证流程不中断 ----
   191→              log.warn(`⚠️ [GEMINI_TOOL_RESCUE] Could not find function name for tool_call_id '${toolCallId}'. Sending as plain text.`);
   192→              parts.push({
   193→                text: typeof message.content === 'string' ? message.content :
   194→                  JSON.stringify(message.content)
   195→              });
   196→            }
   197→          } else { // 包括 "user" 和 "system"
   198→            role = "user";
   199→
   200→            // 处理 user 或 system 的文本内容
   201→            if (typeof message.content === "string") {
   202→              parts.push({ text: message.content });
   203→            } else if (Array.isArray(message.content)) {
   204→              parts.push(
   205→                ...message.content.map((content) => {
   206→                  if (content.type === "text") {
   207→                    return {
   208→                      text: content.text || "",
   209→                    };
   210→                  }
   211→                  return { text: "" };
   212→                }).filter(part => part.text !== "")
   213→              );
   214→            }
   215→          }
   216→          
   217→          return {
   218→            role,
   219→            parts,
   220→          };
   221→        }),
   222→        tools: request.tools && request.tools.length > 0 ? [
   223→          {
   224→            functionDeclarations:
   225→              request.tools.map((tool: UnifiedTool) => {
   226→                const cleanedParameters: any = {
   227→                  type: "object",
   228→                };
   229→
   230→                if (tool.function.parameters) {
   231→                  if (tool.function.parameters.properties) {
   232→                    // Use the new recursive helper function
   233→                    cleanedParameters.properties = cleanSchemaProperties(tool.function.parameters.properties);
   234→                  }
   235→                  if (Array.isArray(tool.function.parameters.required) &&
   236→                      tool.function.parameters.required.length > 0) {
   237→                    cleanedParameters.required = tool.function.parameters.required;
   238→                  }
   239→                }
   240→
   241→                return {
   242→                  name: tool.function.name,
   243→                  description: tool.function.description,
   244→                  parameters: cleanedParameters,
   245→                };
   246→              }) || [],
   247→          },
   248→        ] : undefined, // 如果没有工具，则不发送该字段
   249→        generationConfig: {
   250→          thinkingConfig: {
   251→            includeThoughts: true,
   252→            thinkingBudget: 10000
   253→          },
   254→          ...(request.max_tokens && { maxOutputTokens: request.max_tokens }),
   255→          ...(request.temperature && { temperature: request.temperature })
   256→        },
   257→      },
   258→      config: {
   259→        url: new URL(
   260→          `./${request.model}:${
   261→            request.stream ? "streamGenerateContent?alt=sse" : "generateContent"
   262→          }`,
   263→          provider.baseUrl
   264→        ),
   265→        headers: {
   266→          "x-goog-api-key": provider.apiKey,
   267→          Authorization: undefined,
   268→        },
   269→      },
   270→    };
   271→  }
   272→
   273→  transformRequestOut(request: Record<string, any>): UnifiedChatRequest {
   274→    const contents: GeminiContent[] = request.contents || [];
   275→    const tools: GeminiTool[] = request.tools || [];
   276→    const model: string = request.model;
   277→    const max_tokens: number | undefined = request.max_tokens;
   278→    const temperature: number | undefined = request.temperature;
   279→    const stream: boolean | undefined = request.stream;
   280→    const tool_choice = request.tool_choice;
   281→
   282→    const unifiedChatRequest: UnifiedChatRequest = {
   283→      messages: [],
   284→      model,
   285→      max_tokens,
   286→      temperature,
   287→      stream,
   288→      tool_choice,
   289→    };
   290→
   291→    contents.forEach((content) => {
   292→      const message: UnifiedMessage = {
   293→        role: content.role === "model" ? "assistant" : "user",
   294→        content: content.parts
   295→          .filter(part => part.text)
   296→          .map(part => ({
   297→            type: "text" as const,
   298→            text: part.text || "",
   299→          })),
   300→      };
   301→      
   302→      // 处理工具调用
   303→      const toolCalls = content.parts
   304→        .filter(part => part.functionCall)
   305→        .map(part => ({
   306→          id: part.functionCall?.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   307→          type: "function" as const,
   308→          function: {
   309→            name: part.functionCall?.name || "",
   310→            arguments: JSON.stringify(part.functionCall?.args || {}),
   311→          },
   312→        }));
   313→      
   314→      if (toolCalls.length > 0) {
   315→        message.tool_calls = toolCalls;
   316→      }
   317→      
   318→      unifiedChatRequest.messages.push(message);
   319→    });
   320→
   321→    if (tools.length > 0) {
   322→      unifiedChatRequest.tools = [];
   323→      tools.forEach((tool) => {
   324→        if (tool.functionDeclarations) {
   325→          tool.functionDeclarations.forEach((funcDecl) => {
   326→            unifiedChatRequest.tools!.push({
   327→              type: "function",
   328→              function: {
   329→                name: funcDecl.name,
   330→                description: funcDecl.description,
   331→                parameters: {
   332→                  type: "object",
   333→                  properties: funcDecl.parameters.properties || {},
   334→                  required: funcDecl.parameters.required || [],
   335→                  additionalProperties: funcDecl.parameters.additionalProperties
   336→                },
   337→              },
   338→            });
   339→          });
   340→        }
   341→      });
   342→    }
   343→
   344→    return unifiedChatRequest;
   345→  }
