   347→  async transformResponseOut(response: Response): Promise<Response> {
   348→    if (response.headers.get("Content-Type")?.includes("application/json")) {
   349→      return this.handleNonStreamResponse(response);
   350→    } else if (response.headers.get("Content-Type")?.includes("stream")) {
   351→      return this.handleStreamResponse(response);
   352→    }
   353→    return response;
   354→  }
   355→
   356→  private async handleNonStreamResponse(response: Response): Promise<Response> {
   357→    // 检查响应状态
   358→    if (!response.ok) {
   359→      const errorText = await response.text();
   360→      log.error('❌ [GEMINI_ERROR] Gemini API 错误响应:', response.status, response.statusText);
   361→      log.error('❌ [GEMINI_ERROR] 错误详情:', errorText);
   362→      throw new Error(`Gemini API error: ${response.status} ${response.statusText} - ${errorText}`);
   363→    }
   364→    
   365→    // 记录原始响应内容
   366→    const responseClone = response.clone();
   367→    const rawResponseText = await responseClone.text();
   368→    log.info('🔍 [GEMINI_RAW_RESPONSE] 服务器原始响应:', rawResponseText);
   369→    
   370→    const jsonResponse: any = JSON5.parse(rawResponseText);
   371→    
   372→    // 检查是否有错误信息
   373→    if (jsonResponse.error) {
   374→      log.error('❌ [GEMINI_ERROR] Gemini API 返回错误:', jsonResponse.error);
   375→      throw new Error(`Gemini API error: ${jsonResponse.error.message || 'Unknown error'}`);
   376→    }
   377→    
   378→    if (!jsonResponse.candidates || !jsonResponse.candidates[0]) {
   379→      throw new Error("Invalid Gemini response format");
   380→    }
   381→    
   382→    // 检查 finishReason 是否为错误状态
   383→    const candidate = jsonResponse.candidates[0];
   384→    if (candidate.finishReason === "MALFORMED_FUNCTION_CALL") {
   385→      log.warn('⚠️ [GEMINI_MALFORMED_FUNCTION] 检测到工具调用格式错误');
   386→      // 返回一个空的成功响应，而不是抛出错误
   387→      const errorRes = {
   388→        id: jsonResponse.responseId || `chatcmpl-${Date.now()}`,
   389→        choices: [
   390→          {
   391→            finish_reason: "stop",
   392→            index: 0,
   393→            message: {
   394→              content: "抱歉，工具调用格式有误，无法执行。",
   395→              role: "assistant" as const,
   396→            },
   397→          },
   398→        ],
   399→        created: Math.floor(Date.now() / 1000),
   400→        model: jsonResponse.modelVersion || "gemini-pro",
   401→        object: "chat.completion" as const,
   402→        usage: jsonResponse.usageMetadata ? {
   403→          completion_tokens: jsonResponse.usageMetadata.candidatesTokenCount,
   404→          prompt_tokens: jsonResponse.usageMetadata.promptTokenCount,
   405→          total_tokens: jsonResponse.usageMetadata.totalTokenCount,
   406→        } : undefined,
   407→      };
   408→      
   409→      return new Response(JSON.stringify(errorRes), {
   410→        status: 200, // 返回200而不是错误状态
   411→        statusText: "OK",
   412→        headers: response.headers,
   413→      });
   414→    }
   415→    const tool_calls = candidate.content.parts
   416→      .filter((part: GeminiPart) => part.functionCall)
   417→      .map((part: GeminiPart) => {
   418→        // 安全处理 arguments
   419→        let args = part.functionCall?.args || {};
   420→        let argsString = "";
   421→        
   422→        try {
   423→          if (typeof args === "string") {
   424→            // 如果已经是字符串，直接使用
   425→            argsString = args;
   426→          } else {
   427→            // 如果是对象，序列化为字符串
   428→            argsString = JSON.stringify(args);
   429→          }
   430→        } catch (error) {
   431→          log.warn('⚠️ [GEMINI_TOOL_ARGS_ERROR] 工具参数序列化失败:', error);
   432→          argsString = "{}";
   433→        }
   434→        
   435→        return {
   436→          id: part.functionCall?.id || `tool_${Math.random().toString(36).substring(2, 15)}`,
   437→          type: "function" as const,
   438→          function: {
   439→            name: part.functionCall?.name || "",
   440→            arguments: argsString,
   441→          },
   442→        };
   443→      });
   444→
   445→    const res = {
   446→      id: jsonResponse.responseId || `chatcmpl-${Date.now()}`,
   447→      choices: [
   448→        {
   449→          finish_reason: candidate.finishReason?.toLowerCase() || null,
   450→          index: 0,
   451→          message: {
   452→            content: candidate.content.parts
   453→              .filter((part: GeminiPart) => {
   454→                // 过滤思考块，只做日志
   455→                if (part.thought === true) {
   456→                  log.info(`🧠 [GEMINI_THOUGHT] 捕获到思考块 (非流式): "${part.text}"`);
   457→                  return false;
   458→                }
   459→                return !!part.text;
   460→              })
   461→              .map((part: GeminiPart) => part.text)
   462→              .join(""),
   463→            role: "assistant" as const,
   464→            tool_calls: tool_calls.length > 0 ? tool_calls : undefined,
   465→          },
   466→        },
   467→      ],
   468→      created: Math.floor(Date.now() / 1000),
   469→      model: jsonResponse.modelVersion || "gemini-pro",
   470→      object: "chat.completion" as const,
   471→      usage: jsonResponse.usageMetadata ? {
   472→        completion_tokens: jsonResponse.usageMetadata.candidatesTokenCount,
   473→        prompt_tokens: jsonResponse.usageMetadata.promptTokenCount,
   474→        total_tokens: jsonResponse.usageMetadata.totalTokenCount,
   475→      } : undefined,
   476→    };
   477→
   478→    return new Response(JSON.stringify(res), {
   479→      status: response.status,
   480→      statusText: response.statusText,
   481→      headers: response.headers,
   482→    });
   483→  }
