   485→  private handleStreamResponse(response: Response): Response {
   486→    if (!response.body) {
   487→      return response;
   488→    }
   489→
   490→    const decoder = new TextDecoder();
   491→    const encoder = new TextEncoder();
   492→    const stream = new ReadableStream({
   493→      async start(controller) {
   494→        const reader = response.body!.getReader();
   495→        let buffer = "";
   496→        let usageMetadata: any = null;
   497→        let hasInjectedThinking = false;
   498→        let hasProducedContent = false; // <-- 添加内容追踪标志
   499→        let blockCounter = 0; // 🔍 添加数据块计数器
   500→
   501→        // SSE 解析状态机
   502→        let state = 'WAITING_DATA'; // WAITING_DATA, IN_DATA, WAITING_END
   503→        let currentData = "";
   504→        let dataLines: string[] = [];
   505→
   506→        try {
   507→          while (true) {
   508→            const { done, value } = await reader.read();
   509→            if (done) {
   510→              // 流结束，处理最后的缓冲区
   511→              if (buffer.trim()) {
   512→                log.warn('⚠️ [GEMINI_STREAM_WARN] 流结束时缓冲区中仍有未处理数据:', buffer.substring(0, 200) + '...');
   513→                await processBuffer(buffer, true); // 强制处理
   514→              }
   515→              
   516→              // <-- 在这里注入空内容块
   517→              if (!hasProducedContent) {
   518→                log.info('🟡 [GEMINI_EMPTY_STREAM] 为保证消息合法，注入一个空内容块');
   519→                const emptyContentChunk = {
   520→                  choices: [{
   521→                    delta: { role: "assistant", content: "" },
   522→                    index: 0,
   523→                    finish_reason: null,
   524→                  }],
   525→                  created: parseInt(String(Date.now() / 1000)),
   526→                  id: "empty_content_fix",
   527→                  model: "gemini-pro",
   528→                  object: "chat.completion.chunk",
   529→                };
   530→                controller.enqueue(encoder.encode(`data: ${JSON.stringify(emptyContentChunk)}\n\n`));
   531→              }
   532→              
   533→              // 发送最终的结束块
   534→              log.info('🏁 [GEMINI_STREAM_END] 流真正结束，发送最终块');
   535→              const finalRes: any = {
   536→                choices: [
   537→                  {
   538→                    delta: {
   539→                      role: "assistant",
   540→                      content: null,
   541→                    },
   542→                    finish_reason: "stop",
   543→                    index: 0,
   544→                    logprobs: null,
   545→                  },
   546→                ],
   547→                created: parseInt(new Date().getTime() / 1000 + "", 10),
   548→                id: "final",
   549→                model: "gemini-pro",
   550→                object: "chat.completion.chunk",
   551→                system_fingerprint: "fp_a49d71b8a1",
   552→              };
   553→              
   554→              if (usageMetadata) {
   555→                finalRes.usage = {
   556→                  completion_tokens: usageMetadata.candidatesTokenCount || 0,
   557→                  prompt_tokens: usageMetadata.promptTokenCount || 0,
   558→                  total_tokens: usageMetadata.totalTokenCount || 0,
   559→                };
   560→              }
   561→              
   562→              const finalChunk = `data: ${JSON.stringify(finalRes)}\n\n`;
   563→              log.info('🏁 [GEMINI_FINAL_CHUNK] 最终块内容:', finalChunk);
   564→              
   565→              controller.enqueue(encoder.encode(finalChunk));
   566→              break;
   567→            }
   568→
   569→            // 将新数据追加到缓冲区
   570→            const newData = decoder.decode(value, { stream: true });
   571→            // console.log("Gemini Raw Stream Data:", newData);
   572→            buffer += newData;
   573→            
   574→            // 🔍 只记录包含思考token的数据块
   575→            // if (newData.includes('thoughtsTokenCount') && newData.includes('"thoughtsTokenCount":')) {
   576→            //   log.info('🔍 [GEMINI_RAW_STREAM] 收到包含思考token的数据');
   577→            // }
   578→            
   579→            // 处理缓冲区
   580→            await processBuffer(buffer, false);
   581→          }
   582→        } catch (error) {
   583→          log.error('❌ [GEMINI_STREAM_ERROR] 流处理错误:', error);
   584→          // 发送错误消息给客户端
   585→          try {
   586→            const errorRes = {
   587→              choices: [
   588→                {
   589→                  delta: {
   590→                    role: "assistant",
   591→                    content: "抱歉，处理响应时出现错误。",
   592→                  },
   593→                  finish_reason: "stop",
   594→                  index: 0,
   595→                  logprobs: null,
   596→                },
   597→              ],
   598→              created: parseInt(new Date().getTime() / 1000 + "", 10),
   599→              id: "",
   600→              model: "",
   601→              object: "chat.completion.chunk",
   602→              system_fingerprint: "fp_a49d71b8a1",
   603→            };
   604→            controller.enqueue(encoder.encode(`data: ${JSON.stringify(errorRes)}\n\n`));
   605→          } catch (finalError) {
   606→            log.error('❌ [GEMINI_FINAL_ERROR] 发送错误消息失败:', finalError);
   607→          }
   608→        } finally {
   609→          controller.close();
   610→        }
   611→
   612→        // 处理缓冲区的函数
   613→        async function processBuffer(bufferData: string, isEnd: boolean) {
   614→          // 🔍 只记录包含思考token的缓冲区
   615→          // if (bufferData.includes('thoughtsTokenCount') && bufferData.includes('"thoughtsTokenCount":')) {
   616→          //   log.info('🔍 [GEMINI_BUFFER_PROCESS] 处理包含思考token的缓冲区');
   617→          // }
   618→          const lines = bufferData.split('\n');
   619→          let remainingBuffer = "";
   620→          
   621→          for (let i = 0; i < lines.length; i++) {
   622→            const line = lines[i];
   623→            
   624→            switch (state) {
   625→              case 'WAITING_DATA':
   626→                if (line.startsWith('data: ')) {
   627→                  // 开始新的data块
   628→                  currentData = line.slice(6); // 移除 'data: ' 前缀
   629→                  dataLines = [currentData];
   630→                  state = 'IN_DATA';
   631→                  // 🔍 只记录包含思考token的数据块开始
   632→                  // if (currentData.includes('thoughtsTokenCount')) {
   633→                  //   log.info('🔍 [GEMINI_DATA_START] 开始包含思考token的数据块');
   634→                  // }
   635→                } else if (line.trim() !== '') {
   636→                  // 非空行但不是data开头，可能是其他SSE字段，忽略
   637→                  log.warn('⚠️ [GEMINI_UNKNOWN_LINE] 未知行:', line.substring(0, 100) + '...');
   638→                }
   639→                break;
   640→                
   641→              case 'IN_DATA':
   642→                if (line.startsWith('data: ')) {
   643→                  // 遇到新的data块，先处理当前块
   644→                  await processCompleteDataBlock();
   645→                  
   646→                  // 开始新的data块
   647→                  currentData = line.slice(6);
   648→                  dataLines = [currentData];
   649→                } else if (line.trim() === '') {
   650→                  // 空行表示data块结束
   651→                  // 🔍 只记录包含思考token的数据块结束
   652→                  // if (dataLines.some(line => line.includes('thoughtsTokenCount'))) {
   653→                  //   log.info('🔍 [GEMINI_DATA_END] 包含思考token的数据块结束，准备处理');
   654→                  // }
   655→                  await processCompleteDataBlock();
   656→                  state = 'WAITING_DATA';
   657→                } else {
   658→                  // 继续当前data块
   659→                  dataLines.push(line);
   660→                }
   661→                break;
   662→            }
   663→          }
   664→          
   665→          // 如果不是流结束，保留最后一行作为缓冲区（可能不完整）
   666→          if (!isEnd && lines.length > 0) {
   667→            const lastLine = lines[lines.length - 1];
   668→            if (state === 'IN_DATA' && !lastLine.startsWith('data: ') && lastLine.trim() !== '') {
   669→              // 当前在data块中，最后一行可能不完整，保留在缓冲区
   670→              remainingBuffer = lastLine;
   671→            } else if (state === 'WAITING_DATA' && lastLine.startsWith('data: ')) {
   672→              // 最后一行是data开头，但可能不完整
   673→              remainingBuffer = lastLine;
   674→            }
   675→          }
   676→          
   677→          // 更新缓冲区
   678→          buffer = remainingBuffer;
   679→        }
   680→        
   681→        // 处理完整的data块
   682→        async function processCompleteDataBlock() {
   683→          
   684→          if (dataLines.length === 0) return;
   685→          
   686→          // 合并所有行
   687→          const jsonStr = dataLines.join('\n').trim();
   688→          // log.info(`RAW_GEMINI_CHUNK: ${jsonStr}`);
   689→          if (!jsonStr) return;
   690→          
   691→          blockCounter++; // 🔍 增加数据块计数器
   692→          // log.info('🔍 [GEMINI_PROCESSING_BLOCK] 处理第' + blockCounter + '个data块，长度:', jsonStr.length);
   693→          
   694→          let chunk: any;
   695→          try {
   696→            chunk = JSON5.parse(jsonStr);
   697→            
   698→                      // 🔍 只记录包含思考token的数据块
   699→          // if (chunk.usageMetadata && chunk.usageMetadata.thoughtsTokenCount > 0) {
   700→          //   log.info('🧠 [GEMINI_THINKING_DETECTED] 第' + blockCounter + '块: 思考token=' + chunk.usageMetadata.thoughtsTokenCount + ', parts=' + (chunk.candidates?.[0]?.content?.parts?.length || 0));
   701→          // }
   702→          
   703→          if (chunk.usageMetadata) {
   704→            usageMetadata = chunk.usageMetadata;
   705→          }
   706→          } catch (parseError: any) {
   707→            log.error('❌ [GEMINI_JSON_PARSE_ERROR] JSON解析失败:', parseError);
   708→            log.error('❌ [GEMINI_JSON_CONTENT] 内容:', jsonStr.substring(0, 300) + '...');
   709→            return; // 跳过这个块
   710→          }
   711→
   712→          if (!hasInjectedThinking && (chunk.candidates?.[0]?.content?.parts || chunk.usageMetadata)) {
   713→            hasInjectedThinking = true;
   714→            const thinkingRes = {
   715→              choices: [
   716→                {
   717→                  delta: {
   718→                    role: "assistant",
   719→                    thinking: true,
   720→                  },
   721→                  finish_reason: null,
   722→                  index: 0,
   723→                },
   724→              ],
   725→              created: parseInt(new Date().getTime() / 1000 + "", 10),
   726→              id: (chunk as any).responseId || "thinking_id",
   727→              model: (chunk as any).modelVersion || "gemini-pro",
   728→              object: "chat.completion.chunk",
   729→              system_fingerprint: "fp_a49d71b8a1",
   730→            };
   731→            controller.enqueue(encoder.encode(`data: ${JSON.stringify(thinkingRes)}\n\n`));
   732→            log.info('✨ [THINKING_EVENT] 注入 "思考中" 事件');
