   856→            id: (chunk as any).responseId || "",
   857→            model: (chunk as any).modelVersion || "",
   858→            object: "chat.completion.chunk",
   859→            system_fingerprint: "fp_a49d71b8a1",
   860→          };
   861→
   862→          if ((chunk as any).usageMetadata) {
