   864→              completion_tokens: (chunk as any).usageMetadata.candidatesTokenCount,
   865→              prompt_tokens: (chunk as any).usageMetadata.promptTokenCount,
   866→              total_tokens: (chunk as any).usageMetadata.totalTokenCount,
   867→            };
   868→          }
   869→
   870→          controller.enqueue(encoder.encode(`data: ${JSON.stringify(res)}\n\n`));
   871→        }
   872→      },
   873→    });
   874→
   875→    return new Response(stream, {
