     8â†’// vvvvvvvvvvvv NEW HELPER FUNCTION vvvvvvvvvvvv
     9â†’/**
    10â†’ * Recursively cleans the properties of a JSON schema to keep only fields supported by Gemini.
    11â†’ * @param properties The properties object to clean.
    12â†’ * @returns A new, cleaned properties object.
    13â†’ */
    14â†’function cleanSchemaProperties(properties: Record<string, any>): Record<string, any> {
    15â†’  const cleanedProperties: Record<string, any> = {};
    16â†’
    17â†’  Object.keys(properties).forEach((key) => {
    18â†’    const prop = properties[key];
    19â†’    const cleanedProp: any = {};
    20â†’
    21â†’    // Strict whitelist of supported fields
    22â†’    cleanedProp.type = prop.type;
    23â†’    if (prop.description) cleanedProp.description = prop.description;
    24â†’    if (prop.enum) cleanedProp.enum = prop.enum;
    25â†’
    26â†’    // Recursive cleaning for array items
    27â†’    if (prop.type === 'array' && prop.items && typeof prop.items === 'object') {
    28â†’      cleanedProp.items = {
    29â†’        type: prop.items.type,
    30â†’      };
    31â†’      if (prop.items.description) {
    32â†’        cleanedProp.items.description = prop.items.description;
    33â†’      }
    34â†’      // *** THIS IS THE KEY RECURSIVE FIX ***
    35â†’      if (prop.items.properties) {
    36â†’        cleanedProp.items.properties = cleanSchemaProperties(prop.items.properties);
    37â†’      }
    38â†’       if (prop.items.required) {
    39â†’        cleanedProp.items.required = prop.items.required;
    40â†’      }
    41â†’    }
    42â†’
    43â†’    // Recursive cleaning for object properties
    44â†’    if (prop.type === 'object' && prop.properties) {
    45â†’        cleanedProp.properties = cleanSchemaProperties(prop.properties);
    46â†’    }
    47â†’
    48â†’    cleanedProperties[key] = cleanedProp;
    49â†’  });
    50â†’
    51â†’  return cleanedProperties;
    52â†’}
