   164â†’            }
   165â†’
   166â†’          } else if (message.role === "tool") {
   167â†’            role = "user"; // è§’è‰²æ˜ å°„ä¿æŒä¸å˜
   168â†’
   169â†’            const toolCallId = message.tool_call_id;
