   289â†’  transformRequestOut(request: Record<string, any>): UnifiedChatRequest {
   290â†’    const contents: GeminiContent[] = request.contents || [];
   291â†’    const tools: GeminiTool[] = request.tools || [];
   292â†’    const model: string = request.model;
