   363â†’  async transformResponseOut(response: Response): Promise<Response> {
   364â†’    if (response.headers.get("Content-Type")?.includes("application/json")) {
   365â†’      return this.handleNonStreamResponse(response);
   366â†’    } else if (response.headers.get("Content-Type")?.includes("stream")) {
