   372â†’  private async handleNonStreamResponse(response: Response): Promise<Response> {
   373â†’    // æ£€æŸ¥å“åº”çŠ¶æ€
   374â†’    if (!response.ok) {
   375â†’      const errorText = await response.text();
   376â†’      log.error('âŒ [GEMINI_ERROR] Gemini API é”™è¯¯å“åº”:', response.status, response.statusText);
   377â†’      log.error('âŒ [GEMINI_ERROR] é”™è¯¯è¯¦æƒ…:', errorText);
