   384â†’    log.info('ğŸ” [GEMINI_RAW_RESPONSE] æœåŠ¡å™¨åŸå§‹å“åº”:', rawResponseText);
   385â†’    
   386â†’    const jsonResponse: any = JSON5.parse(rawResponseText);
