   407â†’            finish_reason: "stop",
   408â†’            index: 0,
   409â†’            message: {
   410â†’              content: "æŠ±æ­‰ï¼Œå·¥å…·è°ƒç”¨æ ¼å¼æœ‰è¯¯ï¼Œæ— æ³•æ‰§è¡Œã€‚",
   411â†’              role: "assistant" as const,
   412â†’            },
