     6â†’// vvvvvvvvvvvv NEW HELPER FUNCTION vvvvvvvvvvvv
     7â†’/**
     8â†’ * Recursively cleans the properties of a JSON schema to keep only fields supported by Gemini.
     9â†’ * @param properties The properties object to clean.
    10â†’ * @returns A new, cleaned properties object.
    11â†’ */
    12â†’function cleanSchemaProperties(properties: Record<string, any>): Record<string, any> {
    13â†’  const cleanedProperties: Record<string, any> = {};
    14â†’
    15â†’  Object.keys(properties).forEach((key) => {
    16â†’    const prop = properties[key];
    17â†’    const cleanedProp: any = {};
    18â†’
    19â†’    // Strict whitelist of supported fields
    20â†’    cleanedProp.type = prop.type;
    21â†’    if (prop.description) cleanedProp.description = prop.description;
    22â†’    if (prop.enum) cleanedProp.enum = prop.enum;
    23â†’
    24â†’    // Recursive cleaning for array items
    25â†’    if (prop.type === 'array' && prop.items && typeof prop.items === 'object') {
    26â†’      cleanedProp.items = {
    27â†’        type: prop.items.type,
    28â†’      };
    29â†’      if (prop.items.description) {
    30â†’        cleanedProp.items.description = prop.items.description;
    31â†’      }
    32â†’      // *** THIS IS THE KEY RECURSIVE FIX ***
    33â†’      if (prop.items.properties) {
    34â†’        cleanedProp.items.properties = cleanSchemaProperties(prop.items.properties);
    35â†’      }
    36â†’       if (prop.items.required) {
    37â†’        cleanedProp.items.required = prop.items.required;
    38â†’      }
    39â†’    }
    40â†’
    41â†’    // Recursive cleaning for object properties
    42â†’    if (prop.type === 'object' && prop.properties) {
    43â†’        cleanedProp.properties = cleanSchemaProperties(prop.properties);
    44â†’    }
    45â†’
    46â†’    cleanedProperties[key] = cleanedProp;
    47â†’  });
    48â†’
    49â†’  return cleanedProperties;
    50â†’}
    51â†’// ^^^^^^^^^^^^ END OF HELPER FUNCTION ^^^^^^^^^^^^
    52â†’
